        return this.http.get("https://url-shortner-418d4-default-rtdb.asia-southeast1.firebasedatabase.app/shortened_urls/" + short_url + "/fullink.json").toPromise();
    }
    getHisLinks(userId) {
        return this.http.get("https://url-shortner-418d4-default-rtdb.asia-southeast1.firebasedatabase.app/users/" + userId + "/links.json");
