import { __decorate } from "tslib";
import { Component } from '@angular/core';
let RedirectionComponent = class RedirectionComponent {
    constructor(_service, _router) {
        this._service = _service;
        this._router = _router;
    }
    async ngOnInit() {
        var res = await this._service.viewLink(this._router.snapshot.url[0]['path']);
        console.log(res);
        if (res) {
            window.location.href = String(res);
        }
    }
};
RedirectionComponent = __decorate([
    Component({
        selector: 'app-redirection',
        templateUrl: './redirection.component.html',
        styleUrls: ['./redirection.component.scss']
    })
], RedirectionComponent);
export { RedirectionComponent };
